import React from 'react';
import AssignedSurveys from './assigned_surveys';
import SurveyList from './survey_list';

let surveys= [
    {sid:1, name:'survey 1', eid:1}, 
    {sid:2, name:'survey 2', eid:1}, 
    {sid:3, name:'survey 3', eid:1},
    {sid:4, name:'survey 4', eid:1},
    {sid:5, name:'survey 5', eid:1},
    {sid:6, name:'survey 6', eid:1}]

// let assignedSurveys=[{sid:7, name:'survey 7', eid:1}]

class Survey extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            surveys: surveys,
            assignedSurveys: assignedSurveys
        }
        this.assignSurvey = this.assignSurvey.bind(this)
    }

    assignSurvey(sid){
        sid = parseInt(sid)
        let addSurvey = ""
        var indx
        for (indx in this.state.surveys){
            if (this.state.surveys[indx].sid === sid){
                console.log(sid)
                addSurvey = this.state.surveys[indx]
            }
        }

        let newAss = [...this.state.assignedSurveys, addSurvey]
        console.log(newAss)
        this.setState({assignedSurveys: newAss})

        console.log(this.state.assignedSurveys)

    }

    render(){
        return(
            <div className="columns">
                    <SurveyList surveys={this.state.surveys} addSurvey = {this.assignSurvey}/>
                    <AssignedSurveys assSurveys={this.state.assignedSurveys}/>

            </div>
        )
    }
}

export default Survey
