import React from 'react';
import Tables from './tables';


class SurveyList extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            surveys: this.props.surveys,
            search:"",
        }
    }

    componentDidUpdate(prevProps){
        if(this.props !== prevProps){
         this.setState({
          surveys:this.props.surveys,
         })
        }}

    changeSearch(e){
        const value = e.target.value
        this.setState({
            search: value
        })
    }

    render(){
        let {surveys} = this.state;
        surveys = surveys.filter(survey=>{

            return survey.name.indexOf(this.state.search) !==1
        })

        return(
                <div className="column is-centered has-text-centered">
                    Survey List
                    <div className="field">
                        <label className="level-left label is-inline"> Search</label>
                            <input class="input is-inline-block" value={e =>this.state.search(e)} onChange={this.changeSearch} type="text" placeholder="Search Surveys"/>
                        </div>
                        <div class="table-container">
                            <table class="table is-bordered is-striped is-narrow is-fullwidth">
                                <tbody>
                                    {surveys.map( (survey) => {
                                            return <Tables survey={survey.name} 
                                            surdisabled ={"disabled"} 
                                            addSurvey = {this.props.addSurvey} 
                                            sid={survey.sid} 
                                            key={survey.sid}/>
                                    })
                                }
                            </tbody>
                            </table>
                        </div>
                    </div>

        )
    }
}

export default SurveyList
