import Survey from '../survey/survey';

let employee= [
    {assSur:[1,2,7], name:'Employee 1', eid:1}, 
    {assSur:[3,4,5], name:'Employee 2', eid:1}, 
    {assSur:[], name:'Employee 3', eid:1},]

let surveys= [
        {sid:1, name:'survey 1', eid:1}, 
        {sid:2, name:'survey 2', eid:1}, 
        {sid:3, name:'survey 3', eid:1},
        {sid:4, name:'survey 4', eid:1},
        {sid:5, name:'survey 5', eid:1},
        {sid:6, name:'survey 6', eid:1},
        {sid:7, name:'survey 7', eid:1},
        {sid:8, name:'man 8', eid:1}]

let assSurveys=[]

class Employee extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            employees: employee,
            surveysList: surveys,
            assSurveys : assSurveys,
            empSur:[],
            empAss:[],
            value : -1
        }
        this.changeEmp = this.changeEmp.bind(this)
    }

    changeEmp(e){

        console.log(e.target.value);
        this.setState({ value: e.target.value})

        employee = this.state.employees[e.target.value]
        let sur = this.state.surveysList
        let surcopy = sur
        let assSur=[]
        let empAss = [] 

        for (let i in employee.assSur){
            empAss.push(employee.assSur[i])
        }

        for (let i in sur){
            if (empAss.includes(sur[i].sid)){
                assSur.push(sur[i])
                sur
            }
        }

        console.log(assSur)
        console.log(sur)

    }


    render(){
    return(
        <div>
            <div className="columns is-centered">
                <div className="employee has-text-centered">
                <h3>Select Employee</h3>
                    <div class="select">
                        <select onChange={this.changeEmp} value={this.state.value}>
                            <option value={-1}>Select Employee</option>
                            { this.state.employees.map((employee,i) => {
                                return <option value={i}>{employee.name}</option>
                            })}
                        </select>
                        </div>
                </div>
            </div>

            <Survey empSur={this.state.empSur} 
                    empAss={this.state.empAss}/>
        </div>
    )
    }
}

export default Employee;
